import React, { useState, useEffect } from "react";
import axios from "axios";
import './App.css'
import { IonInput, IonItem, IonList } from '@ionic/react';

function FileHandler() {
  const [file, setFile] = useState("")
  const [fileWorksheet, setFileWorksheet] = useState("")
  const [wc, setWC] = useState(-1)
  const [headerRow, setHeaderRow] = useState(-1)


  var xlsx = require('xlsx')

  function handleChange(event) {
    setFile(event.target.files[0])
    event.preventDefault();
    if (event.target.files) {
      const reader = new FileReader();
      reader.onload = (event) => {
        const data = event.target.result;
        const workbook = xlsx.read(data, { type: "array" });
        const sheetName = workbook.SheetNames[0];
        const worksheet = workbook.Sheets[sheetName];
        setFileWorksheet(worksheet)
        const txt = xlsx.utils.sheet_to_json(worksheet);
        setFile(txt)
      };
      reader.readAsArrayBuffer(event.target.files[0]);
    }
  }

  function handleSubmit(event) {
    event.preventDefault()
    console.log(headerRow)
    console.log(typeof(headerRow))
    const submitData = xlsx.utils.sheet_to_json(fileWorksheet, { range: headerRow }); //com uses 4


    const config = {
      headers: { 'Access-Control-Allow-Origin': '*' }
    };
    axios
      .post("http://localhost:5000/files", {
        input: submitData,
      }, config)
      .then((res) => {
        setWC(res.data);
      })
      .catch((er) => {
        console.log(er);
      });

  }

  return (
    <div className="page">
      {(typeof wc.wordCount === 'undefined') ? (
        <p>NO COUNT FILL OUT BELOW</p>
      ) : (
        wc.wordCount.map((wordCount, i) => (
          <p key={i}>{wordCount}</p>
        ))
      )}

      <form onSubmit={handleSubmit}>
        <h1>React File Upload</h1>
        <input type="file" accept=".xlsx" onChange={handleChange} />

        <br></br>
        <p>Follow Up Questions!</p>
        <IonItem>
          <IonInput label="Write number of header line (has the exact text 'English Text')" type="number" placeholder="000" min={0} max={file.length} onIonChange={(e) => setHeaderRow(int(e.target.value))}></IonInput>
        </IonItem>

        {(file === "" || setHeaderRow > file.length || setHeaderRow < 0) ? (
          <button type="submit" disabled>Submit!</button>
        ) : (
          <button type="submit">Submit!</button>
        )}
      </form>


    </div>
  );
}

export default FileHandler;
