import React, { useState, useEffect } from "react";
import axios from "axios";
import './App.css'
function App() {
  const [data, setData] = useState([{}])
  const [file, setFile] = useState("")

  useEffect(() => {
    fetch("/members").then(
      res => res.json()
    ).then(
      data => {
        setData(data)
        console.log(data)
      }
    )
  }, [])

  onFileChange = (event) => {
    this.setFile({
      file: event.target.files[0],
    })
  }

  onFileUpload = () => {
    const formData = newFormData();
    formData.append(
      "myFile",
      this.file,
      this.file.name
    );

    console.log(this.file)
    axios.post("/uploadfile", formData)
  }

  fileData = () => {
    if (this.state.selectedFile) {
      return (
        <div>
          <h2>File Details:</h2>
          <p>
            File Name:{" "}
            {this.state.selectedFile.name}
          </p>

          <p>
            File Type:{" "}
            {this.state.selectedFile.type}
          </p>

          <p>
            Last Modified:{" "}
            {this.state.selectedFile.lastModifiedDate.toDateString()}
          </p>
        </div>
      );
    } else {
      return (
        <div>
          <br />
          <h4>
            Choose before Pressing the Upload
            button
          </h4>
        </div>
      );
    }
  };

  return (
    <div className="page">
      {/* <h1 className="header">Merry Christmas!</h1> */}
      {/* <h2 className ="subtitle">Welcome to... your new site! For all your translation word count needs.</h2> */}

      {(typeof data.members === 'undefined') ? (
        <p>Loading...</p>
      ) : (
        data.members.map((member, i) => (
          <p key={i}>{member}</p>
        ))
      )}

      <div>
        <input
          type="file"
          onChange={onFileChange}
        />
        <button onClick={onFileUpload}>
          Upload!
        </button>
      </div>
      {this.fileData()}
    </div>
  );
}

export default App;
